-- Name: active_credentials; Type: VIEW; Schema: public; Owner: nickfiddes
--

CREATE VIEW public.active_credentials AS
 SELECT c.id,
    c.name,
    c.description,
    c.credential_type,
    c.status,
    c.last_used_at,
    c.last_verified_at,
    c.error_message,
    ch.name AS channel_name,
    ch.icon_class AS channel_icon,
    ch.color AS channel_color,
    s.name AS service_name,
    s.description AS service_description,
    s.service_url,
    c.created_at,
    c.updated_at
   FROM ((public.credentials c
     JOIN public.credential_channels ch ON ((c.channel_id = ch.id)))
     JOIN public.credential_services s ON ((c.service_id = s.id)))
  WHERE (c.status = 'active'::public.credential_status);


